import React, { useReducer } from 'react'
import { Box, Button, Grid, List, ListItem, Typography } from '@mui/material'
import Board from './Board'

const reducer = (state, action) => {
    switch (action.type){
        case 'MOVE':
            return {
                ...state,
                history: state.history.concat({
                    squares: action.payload.squares,
                }),
                xIsNext: !state.xIsNext,
            };
            default:
                return state;
    }
};

export default function Game() {

    const [state, dispatch] = useReducer(reducer, {
        xIsNext: true,
        history: [{squares: Array(9).fill(null)}],
    });

    const {xIsNext, history} = state;
    const handleClick = (i) => {
        const current = history[history.length - 1];
        const squares = current.squares.slice();
        const winner = calculateWinner(squares);
        if(winner || squares[i]) {
            return; 
        } 
        squares[i] = xIsNext? 'X': 'O';
        dispatch({type:'MOVE', payload:{squares}});
    };
    const current = history[history.length - 1];
    const winner = calculateWinner(current.squares); 

    const status = <Typography>Next Player is X</Typography>;
    const moves = (<ListItem><Button>Start the Game</Button></ListItem>);
    const squares = Array(9).fill("");


 return (
    <Grid // center the content
        container
        spacing={0}
        direction="column"
        alignItems="center"
        justifyContent="center"
    >
        <Typography variant='h2'>Tick Tack Toe</Typography>
        <Box className='game-board'>
            <Board squares={ squares }/>
        </Box>
        <Box className='game-info'>
            <Box>{ status }</Box>
            <List>{ moves }</List>   
        </Box>
    </Grid>
  )
}
